    <script>
        function loadHeaderCart() {
            $(""#miniCart"").load(""/AjaxContent/HeaderCart"");
            $(""#numberCart"").load(""/AjaxContent/NumberCart"");
        }
        $("".removecart"").click(function () {
            var productid = $(this).attr(""data-mahh"");
            $.ajax({
                url: ""api/cart/remove"",
                type: ""POST"",
                dataType: ""JSON"",
                data: {
                    productID: productid,
                },
                success: function (result) {
                    if (result.success) {
                        loadHeaderCart();//Load lại Cart
                        window.location = 'cart.html'
                    }
                },
                error: function (rs) {
                    alert(""Xảy ra lỗi"");
                }
            });
        });
        $("".cartItem"").click(function () {
            var productid = $(this).attr(""data-mahh"");
            var soLuong = parseInt($(this");
                WriteLiteral(@").val());
            $.ajax({
                url: ""api/cart/update"",
                type: ""POST"",
                dataType: ""JSON"",
                data: {
                    productID: productid,
                    amount: soLuong
                },
                success: function (result) {
                    if (result.success) {
                        loadHeaderCart();
                        window.location = 'cart.html'
                    }
                },
                error: function (rs) {
                    alert(""Cập nhật giỏ hàng lỗi"");
                }
            });
        });

    </script>
